import React, { useState, useEffect, useMemo } from 'react';
import { Play, Pause, TrendingUp, TrendingDown, Eye, Users, Target, Clock, Filter, Search, RefreshCw, Calendar, BarChart3, AlertCircle } from 'lucide-react';
import Papa from 'papaparse';
import './App.css';

const GOOGLE_SHEETS_CSV_URL = 'https://docs.google.com/spreadsheets/d/130iwAJbV87MxT_p1IecLczMwihGaTim3i3lawoMWsus/export?format=csv&gid=0';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [accountFilter, setAccountFilter] = useState('all');
  const [performanceFilter, setPerformanceFilter] = useState('all');

  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);

  const loadData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Загружаем данные из Google Sheets
      const response = await fetch(GOOGLE_SHEETS_CSV_URL + '&timestamp=' + Date.now());
      const csvData = await response.text();

      // Парсим CSV
      const parsed = Papa.parse(csvData, {
        header: true,
        dynamicTyping: true,
        skipEmptyLines: true,
        delimiter: ',',
        quoteChar: '"'
      });

      // Находим актуальную дату
      const dates = parsed.data.map(row => row.Date).filter(date => date);
      const latestDate = dates[dates.length - 1];

      // Собираем все креативы
      let allUniqueCreatives = new Set();
      let allCreativeHistory = {};
      let allAccounts = new Set();

      parsed.data.forEach(row => {
        if (!row.Date) return;

        for (let i = 1; i <= 28; i++) {
          const account = row[`Account_${i}`];
          const creative = row[`Creative_${i}`];
          const users = parseInt(row[`Users_${i}`]) || 0;

          if (account && creative && creative.trim() !== '') {
            allUniqueCreatives.add(creative);
            allAccounts.add(account);

            if (!allCreativeHistory[creative]) {
              allCreativeHistory[creative] = {
                creative: creative,
                totalUsers: 0,
                daysActive: 0,
                accounts: new Set(),
                lastActiveDate: row.Date,
                history: []
              };
            }

            allCreativeHistory[creative].totalUsers += users;
            allCreativeHistory[creative].accounts.add(account);
            allCreativeHistory[creative].history.push({
              date: row.Date,
              account: account,
              users: users
            });
          }
        }
      });

      // Подсчитываем дни активности
      Object.keys(allCreativeHistory).forEach(creative => {
        const uniqueDates = [...new Set(allCreativeHistory[creative].history.map(h => h.date))];
        allCreativeHistory[creative].daysActive = uniqueDates.length;
        allCreativeHistory[creative].avgUsersPerDay = 
          allCreativeHistory[creative].daysActive > 0 
            ? Math.round((allCreativeHistory[creative].totalUsers / allCreativeHistory[creative].daysActive) * 10) / 10 
            : 0;
      });

      // Находим активные креативы на актуальную дату
      const lastDateRow = parsed.data.find(row => row.Date === latestDate);
      let activeCreativesOnLastDate = [];

      for (let i = 1; i <= 28; i++) {
        const account = lastDateRow[`Account_${i}`];
        const creative = lastDateRow[`Creative_${i}`];
        const users = parseInt(lastDateRow[`Users_${i}`]) || 0;

        if (account && creative && creative.trim() !== '') {
          activeCreativesOnLastDate.push({
            account: account,
            creative: creative,
            users: users
          });
        }
      }

      const activeCreativeNames = [...new Set(activeCreativesOnLastDate.map(item => item.creative))];

      // Создаем финальную аналитику
      const creativeAnalytics = [...allUniqueCreatives].map(creative => {
        const history = allCreativeHistory[creative];
        const isActive = activeCreativeNames.includes(creative);

        const currentAccounts = isActive 
          ? activeCreativesOnLastDate.filter(item => item.creative === creative).map(item => item.account)
          : [];

        const currentUsers = isActive
          ? activeCreativesOnLastDate.filter(item => item.creative === creative).reduce((sum, item) => sum + item.users, 0)
          : 0;

        // Умная логика определения эффективности
        let performance;
        if (history.totalUsers > 200) {
          performance = 'high';
        } else if (history.totalUsers > 50) {
          performance = 'medium';
        } else {
          // Низкую эффективность даем только креативам, которые:
          // 1. Работали больше чем в 3 аккаунтах И
          // 2. Средн./день меньше 2
          if (history.accounts.size > 3 && history.avgUsersPerDay < 2) {
            performance = 'low';
          } else {
            // Новые креативы (до 3 аккаунтов) получают среднюю эффективность
            performance = 'medium';
          }
        }

        return {
          id: creative.replace(/[^a-zA-Z0-9]/g, '_'),
          creative: creative,
          status: isActive ? 'active' : 'free',
          totalUsers: history.totalUsers,
          currentUsers: currentUsers,
          daysActive: history.daysActive,
          accounts: [...history.accounts],
          currentAccounts: currentAccounts,
          lastActiveDate: history.lastActiveDate,
          avgUsersPerDay: history.avgUsersPerDay,
          performance: performance,
          efficiency: history.avgUsersPerDay
        };
      });

      creativeAnalytics.sort((a, b) => {
        if (a.status !== b.status) return a.status === 'active' ? -1 : 1;
        if (a.status === 'active') return b.currentUsers - a.currentUsers;
        return b.totalUsers - a.totalUsers;
      });

      const activeCount = creativeAnalytics.filter(c => c.status === 'active').length;
      const totalCurrentUsers = creativeAnalytics.filter(c => c.status === 'active').reduce((sum, c) => sum + c.currentUsers, 0);

      setDashboardData({
        latestDate: latestDate,
        creativeAnalytics: creativeAnalytics,
        allAccounts: [...allAccounts],
        activeCreativesOnLastDate: activeCreativesOnLastDate,
        summary: {
          totalCreatives: allUniqueCreatives.size,
          activeCreatives: activeCount,
          freeCreatives: allUniqueCreatives.size - activeCount,
          totalAccounts: allAccounts.size,
          totalUsersAllTime: creativeAnalytics.reduce((sum, c) => sum + c.totalUsers, 0),
          totalCurrentUsers: totalCurrentUsers,
          avgUsersPerCreative: Math.round((creativeAnalytics.reduce((sum, c) => sum + c.totalUsers, 0) / allUniqueCreatives.size) * 10) / 10
        }
      });

      setLastUpdate(new Date().toLocaleString('ru-RU'));

    } catch (error) {
      console.error('Ошибка загрузки данных:', error);
      setError('Ошибка загрузки данных из Google Sheets');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadData();

    // Автообновление каждые 5 минут
    const interval = setInterval(loadData, 5 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  // Фильтрация данных
  const filteredCreatives = useMemo(() => {
    if (!dashboardData) return [];

    return dashboardData.creativeAnalytics.filter(creative => {
      const matchesSearch = creative.creative.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          creative.accounts.some(account => account.toLowerCase().includes(searchTerm.toLowerCase()));
      const matchesStatus = statusFilter === 'all' || creative.status === statusFilter;
      const matchesAccount = accountFilter === 'all' || creative.accounts.includes(accountFilter) || creative.currentAccounts.includes(accountFilter);
      const matchesPerformance = performanceFilter === 'all' || creative.performance === performanceFilter;

      return matchesSearch && matchesStatus && matchesAccount && matchesPerformance;
    });
  }, [dashboardData, searchTerm, statusFilter, accountFilter, performanceFilter]);

  if (loading && !dashboardData) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white flex items-center justify-center">
        <div className="text-center">
          <RefreshCw className="w-12 h-12 animate-spin mx-auto mb-4 text-blue-400" />
          <p className="text-xl">Загружаем данные из Google Sheets...</p>
          <p className="text-sm text-gray-400 mt-2">Подключение к вашей таблице</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white flex items-center justify-center">
        <div className="text-center">
          <AlertCircle className="w-16 h-16 text-red-400 mx-auto mb-4" />
          <p className="text-xl text-red-400">Ошибка подключения</p>
          <p className="text-gray-400 mb-4">{error}</p>
          <button 
            onClick={loadData}
            className="bg-blue-600 hover:bg-blue-700 px-6 py-3 rounded-lg flex items-center gap-2 mx-auto transition-colors"
          >
            <RefreshCw className="w-4 h-4" />
            Попробовать снова
          </button>
        </div>
      </div>
    );
  }

  if (!dashboardData) return null;

  const stats = dashboardData.summary;

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white p-6">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
              Creative Analytics Dashboard
            </h1>
            <p className="text-gray-400 mt-2">
              📅 Актуальная дата: <span className="font-semibold text-white">{dashboardData.latestDate}</span> | 
              📊 {stats.totalCreatives} креативов | 
              🏢 {stats.totalAccounts} аккаунтов
            </p>
            {lastUpdate && (
              <p className="text-xs text-gray-500 mt-1">
                🔄 Последнее обновление: {lastUpdate}
              </p>
            )}
          </div>
          <div className="flex items-center gap-4">
            <div className="text-right">
              <div className="text-sm text-gray-400">Google Sheets синхронизация</div>
              <div className="text-lg font-semibold text-green-400">
                {loading ? 'Обновляется...' : '✓ Подключено'}
              </div>
            </div>
            <button 
              onClick={loadData}
              disabled={loading}
              className="bg-blue-600 hover:bg-blue-700 disabled:bg-gray-600 px-4 py-2 rounded-lg flex items-center gap-2 transition-colors"
            >
              <RefreshCw className={`w-4 h-4 ${loading ? 'animate-spin' : ''}`} />
              Обновить
            </button>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-6 gap-4 mb-8">
          <div className="bg-gradient-to-r from-green-600 to-green-700 p-6 rounded-xl shadow-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-green-100 text-sm">🟢 В работе</p>
                <p className="text-3xl font-bold">{stats.activeCreatives}</p>
                <p className="text-green-200 text-xs mt-1">
                  {Math.round((stats.activeCreatives / stats.totalCreatives) * 100)}% от всех
                </p>
              </div>
              <Play className="w-8 h-8 text-green-200" />
            </div>
          </div>

          <div className="bg-gradient-to-r from-gray-600 to-gray-700 p-6 rounded-xl shadow-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-100 text-sm">⚪ Свободные</p>
                <p className="text-3xl font-bold">{stats.freeCreatives}</p>
                <p className="text-gray-200 text-xs mt-1">
                  {Math.round((stats.freeCreatives / stats.totalCreatives) * 100)}% от всех
                </p>
              </div>
              <Pause className="w-8 h-8 text-gray-200" />
            </div>
          </div>

          <div className="bg-gradient-to-r from-blue-600 to-blue-700 p-6 rounded-xl shadow-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-blue-100 text-sm">👥 Сегодня</p>
                <p className="text-2xl font-bold">{stats.totalCurrentUsers.toLocaleString()}</p>
                <p className="text-blue-200 text-xs mt-1">активных пользователей</p>
              </div>
              <Users className="w-8 h-8 text-blue-200" />
            </div>
          </div>

          <div className="bg-gradient-to-r from-purple-600 to-purple-700 p-6 rounded-xl shadow-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-purple-100 text-sm">📈 За все время</p>
                <p className="text-2xl font-bold">{stats.totalUsersAllTime.toLocaleString()}</p>
                <p className="text-purple-200 text-xs mt-1">всего пользователей</p>
              </div>
              <BarChart3 className="w-8 h-8 text-purple-200" />
            </div>
          </div>

          <div className="bg-gradient-to-r from-yellow-600 to-orange-600 p-6 rounded-xl shadow-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-yellow-100 text-sm">🎯 Аккаунты</p>
                <p className="text-3xl font-bold">{stats.totalAccounts}</p>
                <p className="text-yellow-200 text-xs mt-1">активных</p>
              </div>
              <Target className="w-8 h-8 text-yellow-200" />
            </div>
          </div>

          <div className="bg-gradient-to-r from-indigo-600 to-indigo-700 p-6 rounded-xl shadow-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-indigo-100 text-sm">📊 Всего креативов</p>
                <p className="text-3xl font-bold">{stats.totalCreatives}</p>
                <p className="text-indigo-200 text-xs mt-1">в базе</p>
              </div>
              <Eye className="w-8 h-8 text-indigo-200" />
            </div>
          </div>
        </div>

        {/* Filters */}
        <div className="bg-gray-800 p-6 rounded-xl shadow-lg mb-6">
          <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
            <div className="relative">
              <Search className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
              <input
                type="text"
                placeholder="Поиск креативов..."
                className="w-full bg-gray-700 text-white pl-10 pr-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            <select 
              className="bg-gray-700 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
            >
              <option value="all">🔍 Все статусы</option>
              <option value="active">🟢 В работе ({stats.activeCreatives})</option>
              <option value="free">⚪ Свободные ({stats.freeCreatives})</option>
            </select>

            <select 
              className="bg-gray-700 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={accountFilter}
              onChange={(e) => setAccountFilter(e.target.value)}
            >
              <option value="all">🏢 Все аккаунты</option>
              {dashboardData.allAccounts.map(account => (
                <option key={account} value={account}>
                  {account.length > 25 ? account.substring(0, 25) + '...' : account}
                </option>
              ))}
            </select>

            <select 
              className="bg-gray-700 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={performanceFilter}
              onChange={(e) => setPerformanceFilter(e.target.value)}
            >
              <option value="all">📊 Вся эффективность</option>
              <option value="high">🔥 Высокая (200+ пользователей)</option>
              <option value="medium">📈 Средняя (50-200 или новые)</option>
              <option value="low">📉 Низкая (3+ аккаунта, &lt;2/день)</option>
            </select>

            <div className="bg-gray-700 px-4 py-3 rounded-lg text-gray-300 text-sm flex items-center">
              <Filter className="w-4 h-4 mr-2" />
              Показано: {filteredCreatives.length}
            </div>
          </div>
        </div>
      </div>

      {/* Creatives Table */}
      <div className="bg-gray-800 rounded-xl shadow-lg overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-700">
              <tr>
                <th className="text-left p-4 font-semibold">Статус</th>
                <th className="text-left p-4 font-semibold">Креатив</th>
                <th className="text-left p-4 font-semibold">Сейчас пользователей</th>
                <th className="text-left p-4 font-semibold">Всего за время</th>
                <th className="text-left p-4 font-semibold">Текущие аккаунты</th>
                <th className="text-left p-4 font-semibold">📅 Дней активен</th>
                <th className="text-left p-4 font-semibold">📊 Средн./день</th>
                <th className="text-left p-4 font-semibold">🏢 Всего аккаунтов</th>
                <th className="text-left p-4 font-semibold" title="Высокая: 200+ пользователей | Средняя: 50-200 или новые креативы | Низкая: только для креативов в 3+ аккаунтах со средн./день < 2">
                  📈 Эффективность
                  <span className="text-xs text-gray-400 block font-normal">умная оценка</span>
                </th>
              </tr>
            </thead>
            <tbody>
              {filteredCreatives.map((creative, index) => {
                const isActive = creative.status === 'active';
                const performanceColor = creative.performance === 'high' ? 'text-green-400' : 
                                       creative.performance === 'medium' ? 'text-yellow-400' : 'text-red-400';

                return (
                  <tr 
                    key={creative.id} 
                    className={`border-t border-gray-700 hover:bg-gray-750 transition-colors ${
                      index % 2 === 0 ? 'bg-gray-800' : 'bg-gray-825'
                    }`}
                  >
                    <td className="p-4">
                      <div className={`flex items-center gap-2 px-3 py-1 rounded-full text-sm font-medium w-fit ${
                        isActive 
                          ? 'bg-green-900 text-green-300 border border-green-600' 
                          : 'bg-gray-600 text-gray-300 border border-gray-500'
                      }`}>
                        {isActive ? <Play className="w-3 h-3" /> : <Pause className="w-3 h-3" />}
                        {isActive ? 'В работе' : 'Свободен'}
                      </div>
                    </td>
                    <td className="p-4">
                      <div className="font-medium">
                        <div className="text-white font-mono">{creative.creative}</div>
                        <div className="text-sm text-gray-400">
                          {creative.creative.includes('.mp4') ? '🎥 Видео' : '🎨 Креатив'}
                        </div>
                      </div>
                    </td>
                    <td className="p-4">
                      {isActive ? (
                        <div className={`font-mono text-lg font-bold ${creative.currentUsers > 5 ? 'text-green-400' : creative.currentUsers > 2 ? 'text-yellow-400' : 'text-gray-400'}`}>
                          {creative.currentUsers}
                        </div>
                      ) : (
                        <span className="text-gray-500">-</span>
                      )}
                    </td>
                    <td className="p-4">
                      <div className={`font-mono ${performanceColor}`}>
                        {creative.totalUsers.toLocaleString()}
                      </div>
                    </td>
                    <td className="p-4">
                      <div className="space-y-1">
                        {creative.currentAccounts.slice(0, 2).map(account => (
                          <span key={account} className="inline-block px-2 py-1 bg-blue-900 text-blue-300 rounded text-xs mr-1 mb-1">
                            {account.length > 12 ? account.substring(0, 12) + '...' : account}
                          </span>
                        ))}
                        {creative.currentAccounts.length > 2 && (
                          <span className="text-gray-400 text-xs">
                            +{creative.currentAccounts.length - 2} еще
                          </span>
                        )}
                        {creative.currentAccounts.length === 0 && (
                          <span className="text-gray-500 text-sm">Не активен</span>
                        )}
                      </div>
                    </td>
                    <td className="p-4 font-mono">{creative.daysActive}</td>
                    <td className="p-4 font-mono">{creative.avgUsersPerDay}</td>
                    <td className="p-4 font-mono">{creative.accounts.length}</td>
                    <td className="p-4">
                      <div className={`px-3 py-1 rounded-full text-sm font-medium w-fit ${
                        creative.performance === 'high' ? 'bg-green-900 text-green-300' :
                        creative.performance === 'medium' ? 'bg-yellow-900 text-yellow-300' :
                        'bg-red-900 text-red-300'
                      }`}>
                        {creative.performance === 'high' ? '🔥 Высокая' :
                         creative.performance === 'medium' ? '📈 Средняя' :
                         '📉 Низкая'}
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      {filteredCreatives.length === 0 && (
        <div className="text-center py-12">
          <Eye className="w-16 h-16 text-gray-500 mx-auto mb-4" />
          <p className="text-gray-400 text-lg">Креативы не найдены</p>
          <p className="text-gray-500 text-sm">Попробуйте изменить фильтры поиска</p>
        </div>
      )}
    </div>
  );
}

export default App;
